import React, { useEffect, useRef } from 'react';
import { Street } from '../data/districts';

declare global {
  interface Window {
    ymaps: any;
  }
}

interface YandexMapProps {
  streets: Street[];
  learnedStreets: string[];
  onStreetClick?: (street: Street) => void;
}

const YandexMap: React.FC<YandexMapProps> = ({ streets, learnedStreets, onStreetClick }) => {
  const mapRef = useRef<HTMLDivElement>(null);
  const mapInstanceRef = useRef<any>(null);
  const objectsRef = useRef<any[]>([]);

  useEffect(() => {
    // Загрузка API Яндекс Карт
    const script = document.createElement('script');
    script.src = 'https://api-maps.yandex.ru/2.1/?apikey=302af765-6c16-4994-992f-ec50c7f8746c&lang=ru_RU';
    script.async = true;
    script.onload = initMap;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  useEffect(() => {
    if (mapInstanceRef.current) {
      updateStreets();
    }
  }, [streets, learnedStreets]);

  const initMap = () => {
    window.ymaps.ready(() => {
      if (mapRef.current) {
        mapInstanceRef.current = new window.ymaps.Map(mapRef.current, {
          center: [55.76, 37.64], // Центр Москвы
          zoom: 12,
          controls: ['zoomControl', 'fullscreenControl']
        });
        updateStreets();
      }
    });
  };

  const updateStreets = () => {
    // Очистка предыдущих объектов
    objectsRef.current.forEach(obj => {
      mapInstanceRef.current.geoObjects.remove(obj);
    });
    objectsRef.current = [];

    streets.forEach(street => {
      if (street.coordinates.length < 2) return;
      const isLearned = learnedStreets.includes(street.name);

      // Маркер для теста
      const marker = new window.ymaps.Placemark(street.coordinates[0], {
        balloonContent: street.name
      });
      mapInstanceRef.current.geoObjects.add(marker);
      objectsRef.current.push(marker);

      // Полилиния
      const polyline = new window.ymaps.Polyline(
        street.coordinates,
        { balloonContent: street.name },
        {
          strokeColor: isLearned ? '#4CAF50' : '#FF0000',
          strokeWidth: 8,
          opacity: 1,
          zIndex: 1000
        }
      );
      mapInstanceRef.current.geoObjects.add(polyline);
      objectsRef.current.push(polyline);

      // Центрируем карту на изученную улицу
      if (isLearned) {
        mapInstanceRef.current.setCenter(street.coordinates[0], 15, { duration: 300 });
      }
    });
  };

  return <div ref={mapRef} style={{ width: '100%', height: '100%' }} />;
};

export default YandexMap;
